import React from 'react';
import { Link } from 'react-router-dom';
import NavBar from './NavBar';

const films = [
  { id: 1, title: 'la casa de papel', description: 'Description du film 1.', image: 'data:image/jpeg;base64,', rating: 4.5 },
  { id: 2, title: '13 reasons why', description: 'Description du film 2.', image: 'data:image/jpeg;base64,', rating: 3.8 },
  { id: 3, title: 'the orginal', description: 'Description du film 3.', image: 'data:image/jpeg;base64,', rating: 4.2 },
  { id: 4, title: 'vampire diaries', description: 'Description du film 4.', image: 'data:image/jpeg;base64,', rating: 4.0 },
];

const Home = () => {
  return (
    <div>
      <NavBar />
      <h1>Liste de Films</h1>
      <ul>
        {films.map((film) => (
          <li key={film.id}>
            <Link to={`/film/${film.id}`}>
              <h2>{film.title}</h2>
              <img src={film.image} alt={film.title} style={{ maxWidth: '100%' }} />
              <p>Rating: {film.rating}</p>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;